g.script:
        const svg = document.getElementById("background-animation");
        const rainLayer = svg.getElementById("rain-layer");

        // clear any existing children (safe to re-run)
        while (rainLayer.firstChild) rainLayer.removeChild(rainLayer.firstChild);

        for (let i = 0; i < 30; i++) {
            const x = Math.random() * 1920;
            const delay = Math.random() * 5;
            const duration = 1.5 + Math.random() * 2;

            const drop = document.createElementNS("http://www.w3.org/2000/svg", "rect");
            drop.setAttribute("x", x);
            drop.setAttribute("y", "-50");
            drop.setAttribute("width", 2.2);
            drop.setAttribute("height", 25 + Math.random() * 20);
            drop.setAttribute("fill", "url(#rain-grad)");
            drop.setAttribute("opacity", 0.38);

            const anim = document.createElementNS("http://www.w3.org/2000/svg", "animateTransform");
            anim.setAttribute("attributeName", "transform");
            anim.setAttribute("type", "translate");
            anim.setAttribute("values", "0 0; 0 1200");
            anim.setAttribute("dur", duration + "s");
            anim.setAttribute("repeatCount", "indefinite");
            anim.setAttribute("begin", delay + "s");

            drop.appendChild(anim);
            rainLayer.appendChild(drop);
        }
