svg.script:
    (function() {
      const svg = document.getElementById('background-animation');
      const starsG = document.getElementById('stars');

      // create many small twinkling stars
      const STAR_COUNT = 140;
      for (let i = 0; i < STAR_COUNT; i++) {
        const cx = Math.random() * 1920;
        const cy = Math.random() * 520; // upper area more star-dense
        const r = Math.random() * 1.6 + 0.4;
        const opacity = 0.3 + Math.random() * 0.7;

        const s = document.createElementNS('http://www.w3.org/2000/svg', 'circle');
        s.setAttribute('cx', cx);
        s.setAttribute('cy', cy);
        s.setAttribute('r', r);
        s.setAttribute('fill', 'white');
        s.setAttribute('opacity', opacity);
        s.setAttribute('class', 'star');

        // add tiny twinkle animation with random timing
        const dur = (1.5 + Math.random() * 3).toFixed(2) + 's';
        const begin = (Math.random() * 6).toFixed(2) + 's';
        const anim = document.createElementNS('http://www.w3.org/2000/svg', 'animate');
        anim.setAttribute('attributeName', 'opacity');
        anim.setAttribute('values', Math.max(0.05, opacity - 0.2) + ';' + Math.min(1, opacity + 0.3) + ';' + Math.max(0.05, opacity - 0.2));
        anim.setAttribute('dur', dur);
        anim.setAttribute('begin', begin);
        anim.setAttribute('repeatCount', 'indefinite');
        s.appendChild(anim);

        starsG.appendChild(s);
      }

      // shooting stars: choose a shooter, set its animateMotion path, and trigger
      

      // accessibility: reduce motion if user prefers reduced motion
      try {
        const mq = window.matchMedia('(prefers-reduced-motion: reduce)');
        if (mq && mq.matches) {
          // stop shooting stars and reduce twinkle speeds
          // remove scheduled shooters by replacing scheduleNext with no-op
        }
      } catch (e) {}

    })();
